describe KeyMappingsController do

  # This should return the minimal set of attributes required to create a valid
  # KeyMapping. As you add validations to KeyMapping, be sure to
  # update the return value of this method accordingly.
  def valid_attributes
    {}
  end

  describe "GET index" do
    it "assigns all key_mappings as @key_mappings" do
      key_mapping = KeyMapping.create! valid_attributes
      get :index
      assigns(:key_mappings).should eq([key_mapping])
    end
  end

  describe "GET show" do
    it "assigns the requested key_mapping as @key_mapping" do
      key_mapping = KeyMapping.create! valid_attributes
      get :show, :id => key_mapping.id.to_s
      assigns(:key_mapping).should eq(key_mapping)
