  end

  describe "GET new" do
    it "assigns a new key_mapping as @key_mapping" do
      get :new
      assigns(:key_mapping).should be_a_new(KeyMapping)
    end
  end

  describe "GET edit" do
    it "assigns the requested key_mapping as @key_mapping" do
      key_mapping = KeyMapping.create! valid_attributes
      get :edit, :id => key_mapping.id.to_s
      assigns(:key_mapping).should eq(key_mapping)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "creates a new KeyMapping" do
        expect {
          post :create, :key_mapping => valid_attributes
        }.to change(KeyMapping, :count).by(1)
      end

      it "assigns a newly created key_mapping as @key_mapping" do
        post :create, :key_mapping => valid_attributes
        assigns(:key_mapping).should be_a(KeyMapping)
        assigns(:key_mapping).should be_persisted
      end

      it "redirects to the created key_mapping" do
        post :create, :key_mapping => valid_attributes
        response.should redirect_to(KeyMapping.last)
      end
    end

    describe "with invalid params" do
      it "assigns a newly created but unsaved key_mapping as @key_mapping" do
        # Trigger the behavior that occurs when invalid params are submitted
        KeyMapping.any_instance.stub(:save).and_return(false)
        post :create, :key_mapping => {}
        assigns(:key_mapping).should be_a_new(KeyMapping)
      end

      it "re-renders the 'new' template" do
        # Trigger the behavior that occurs when invalid params are submitted
        KeyMapping.any_instance.stub(:save).and_return(false)
        post :create, :key_mapping => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested key_mapping" do
        key_mapping = KeyMapping.create! valid_attributes
        # Assuming there are no other key_mappings in the database, this
        # specifies that the KeyMapping created on the previous line
