      it "redirects to the key_mapping" do
        key_mapping = KeyMapping.create! valid_attributes
        put :update, :id => key_mapping.id, :key_mapping => valid_attributes
        response.should redirect_to(key_mapping)
      end
    end

    describe "with invalid params" do
      it "assigns the key_mapping as @key_mapping" do
